import React from "react";
import { Button } from "react-bootstrap";
import bmp from "../../../assets/images/bmp.png";
import budget from "../../../assets/images/budget.png";
import gameLife from "../../../assets/images/gameLife.png";
import image from "../../../assets/images/image.png";
import spotify from "../../../assets/images/spotify.png";
import "./Project.scss";

const project = (props) => {
	let img = null;
	let overview = null;

	switch (props.project.image) {
		case "bmp":
			img = <img src={bmp} alt="bmp" className="image" />;

			break;

		case "budget":
			img = <img src={budget} alt="budget" className="image" />;

			break;

		case "gameLife":
			img = <img src={gameLife} alt="gameLife" className="image" />;

			break;

		case "spotify":
			img = <img src={spotify} alt="spotify" className="image" />;

			break;

		case "image":
			img = <img src={image} alt="image_processing" className="image" />;

			break;
		default:
			img = null;
	}

	return (
		<div className="project">
			<div className="img_place">{img}</div>
			<div className="overview">
				<div className="name">{props.project.name}</div>
				<div className="overview_text">{props.project.overview}</div>
			</div>
			<a href={props.project.url} className="anchor" target="_blank" rel="noopener noreferrer">
				Visit the source code
			</a>
		</div>
	);
};

export default project;
