import React from "react";
import { Button } from "react-bootstrap";

import bmp from "assets/images/bmp.png";
import budget from "assets/images/budget.png";
import gameLife from "assets/images/gameLife.png";
import image from "assets/images/image.png";
import spotify from "assets/images/spotify.png";

import "./Project.scss";

const project = ({ project }) => {
	let img = null;
	const loadImage = (imageName) => {
		import(`assets/images/${imageName}.png`).then((image) => {
			img = image;
		});
	};

	switch (project.img) {
		case "bmp":
			img = <img src={bmp} alt="bmp" className="image" />;

			break;

		case "budget":
			img = <img src={budget} alt="budget" className="image" />;

			break;

		case "gameLife":
			img = <img src={gameLife} alt="gameLife" className="image" />;

			break;

		case "spotify":
			img = <img src={spotify} alt="spotify" className="image" />;

			break;

		case "image":
			img = <img src={image} alt="image_processing" className="image" />;

			break;
		default:
			img = null;
	}

	return (
		<div class="project">
			<div class="project-inner">
				<div class="project-front">{img}</div>
				{/* <div class="project-front">
					<img src={project.img} alt="bmp" className="image" />
				</div> */}
				<div class="project-back">
					<div>{project.name}</div>
					<div>{project.overview}</div>
					<Button>
						<a href={project.url}>Go to link</a>
					</Button>
				</div>
			</div>
		</div>
	);
};

export default project;
